
import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { 
  Bot, 
  Palette, 
  ShoppingBag, 
  ArrowRight
} from 'lucide-react';

interface ProjectCategoryCardProps {
  title: string;
  description: string;
  icon: React.ReactNode;
  to: string;
  delay?: number;
  bgClass?: string;
  bgImage?: string;
}

const ProjectCategoryCard: React.FC<ProjectCategoryCardProps> = ({ 
  title, 
  description, 
  icon,
  to,
  delay = 0,
  bgClass = "from-portfolio-blue-700 to-portfolio-blue-500",
  bgImage
}) => {
  const cardRef = useRef<HTMLDivElement>(null);
  
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setTimeout(() => {
              entry.target.classList.add('opacity-100', 'translate-y-0');
            }, delay);
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.1 }
    );
    
    if (cardRef.current) observer.observe(cardRef.current);
    
    return () => {
      if (cardRef.current) observer.unobserve(cardRef.current);
    };
  }, [delay]);

  return (
    <div 
      ref={cardRef} 
      className="glass-card rounded-2xl overflow-hidden transition-all duration-700 opacity-0 translate-y-8 h-full flex flex-col"
    >
      <div 
        className={`bg-gradient-to-br ${bgClass} p-8 text-white relative`}
        style={bgImage ? {
          backgroundImage: `linear-gradient(to bottom right, var(--tw-gradient-stops)), url(${bgImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundBlendMode: 'overlay',
        } : undefined}
      >
        <div className="w-16 h-16 rounded-full bg-white/10 backdrop-blur-sm flex items-center justify-center mb-4">
          {icon}
        </div>
        <h3 className="text-2xl font-bold mb-2">{title}</h3>
        <p className="text-white/80">{description}</p>
      </div>
      
      <div className="p-6 flex-grow flex flex-col justify-between">
        <div className="space-y-3">
          {/* Content will depend on the category */}
        </div>
        
        <Link 
          to={to}
          className="mt-6 inline-flex items-center text-portfolio-blue hover:text-portfolio-blue-400 transition-colors group"
        >
          <span>View Projects</span>
          <ArrowRight size={16} className="ml-2 transition-transform group-hover:translate-x-1" />
        </Link>
      </div>
    </div>
  );
};

const Projects: React.FC = () => {
  const titleRef = useRef<HTMLHeadingElement>(null);
  
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('opacity-100');
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.1 }
    );
    
    if (titleRef.current) observer.observe(titleRef.current);
    
    return () => {
      if (titleRef.current) observer.unobserve(titleRef.current);
    };
  }, []);

  return (
    <section
      id="projects"
      className="py-24 bg-portfolio-gray-900"
    >
      <div className="container-fluid">
        <h2 
          ref={titleRef} 
          className="section-heading opacity-0 transition-all duration-1000 animate-fade-in"
        >
          My Projects
        </h2>
        
        <div className="mt-12 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          <ProjectCategoryCard 
            title="Chatbot Projects" 
            description="Intelligent chat solutions for businesses and platforms"
            icon={<Bot size={32} className="text-white" />}
            to="/projects/chatbots"
            delay={100}
            bgClass="from-portfolio-blue-700/90 to-portfolio-blue-500/90"
            bgImage="data:image/jpeg;base64,"
          />
          
          <ProjectCategoryCard 
            title="Graphic Design" 
            description="Creative visual solutions for brands and businesses"
            icon={<Palette size={32} className="text-white" />}
            to="/projects/graphic-design"
            delay={200}
            bgClass="from-purple-700/90 to-purple-500/90"
            bgImage="data:image/jpeg;base64,"
          />
          
          <ProjectCategoryCard 
            title="E-commerce" 
            description="End-to-end e-commerce solutions and strategies"
            icon={<ShoppingBag size={32} className="text-white" />}
            to="/projects/e-commerce"
            delay={300}
            bgClass="from-emerald-700/90 to-emerald-500/90"
            bgImage="data:image/jpeg;base64,"
          />
        </div>
      </div>
    </section>
  );
};

export default Projects;
